#include <bits/stdc++.h>
using namespace std; ifstream fin("croco1.in"); ofstream fout("croco1.out"); int a[105][105] , n , m , cnt1=0 , cnt2=0; void fill(int i , int j , bool c) { a[i][j]=1; if(c) cnt1++; else cnt2++; c=!c; if(a[i+1][j]==0 && i+1 <= n) fill(i+1 , j , c); if(a[i-1][j]==0 && i-1 >= 1) fill(i-1 , j , c); if(a[i][j+1]==0 && j+1 <= m) fill(i , j+1 , c); if(a[i][j-1]==0 && j-1 >= 1) fill(i , j-1 , c); } int main() { fin >> n >> m; for(int i = 1 ; i <= n ; ++i) for(int j = 1 ; j <= m ; ++j) fin >> a[i][j]; int croco=0 , ele=0; for(int i = 1 ; i <= n ; ++i) for(int j = 1 ; j <= m ; ++j) if(a[i][j]==0) { cnt1=0 , cnt2=0; bool c=true; fill(i , j , c); if(cnt1>cnt2) croco+=cnt1 , ele+=cnt2; else croco+=cnt2 , ele+=cnt1; } fout << croco << ' ' << ele; return 0; }
