#include <bits/stdc++.h>
using namespace std; ifstream fin("inordine.in"); ofstream fout("inordine.out"); int n , St[1001] , Dr[1001] , val[1001] , T[1001]; void RSD(int p) { if(p>0) { fout << val[p] << ' '; RSD(St[p]); RSD(Dr[p]); } } void SRD(int p) { if(p>0) { SRD(St[p]); fout << val[p] << ' '; SRD(Dr[p]); } } void SDR(int p) { if(p>0) { SDR(St[p]); SDR(Dr[p]); fout << val[p] << ' '; } } int main() { fin >> n; for(int i = 1 ; i <= n ; i++) { fin >> val[i] >> St[i] >> Dr[i]; T[St[i]] = i; T[Dr[i]] = i; } int aux = 1 , rez; while(aux > 0) { if(rez != 0) rez = aux; aux = T[aux]; } SRD(rez); }
