#include <bits/stdc++.h>
using namespace std; ifstream fin("nivelebin.in"); ofstream fout("nivelebin.out"); int n , St[1001] , Dr[1001] , val[1001] , T[1001] , nivel[1001] , maxi , cnt; void RSD(int p) { if(p > 0) { cnt++; nivel[p] = cnt; RSD(St[p]); RSD(Dr[p]); cnt--; } } int main() { fin >> n; for(int i = 1 ; i <= n ; i++) { fin >> val[i] >> St[i] >> Dr[i]; T[St[i]] = i; T[Dr[i]] = i; } int rez; for(int i = 1 ; i <= n ; i++) if(!T[i]) rez = i; RSD(rez); for(int i = 1 ; i <= n ; i++) if(nivel[i] > maxi) maxi = nivel[i]; fout << maxi << endl; for(int i = 1 ; i <= maxi ; i++) { int cnt = 0; for(int j = 1 ; j <= n ; j++) if(nivel[j] == i) cnt++; fout << cnt << " "; } }
