#include <bits/stdc++.h>
using namespace std; char s[256]; int f[300]; int numar(char ch) { return ch >= '0' && ch <= '9'; } int main() { cin.getline(s , 256); int i = 0; bool sgn = 1; int nr = 0; while(s[i]!='\0') { while(numar(s[i])) { nr=nr*10+(int)s[i]-'0'; i++; } if(s[i]=='-') sgn = false; if(s[i]=='+') sgn = true; if(s[i]=='x' || s[i]=='y' || s[i]=='z') { if(nr == 0) { if(sgn) f[s[i]]++ , nr = 0; else f[s[i]]-- , nr = 0; } if(sgn) f[s[i]]+=nr , nr = 0; else f[s[i]]-=nr , nr = 0; } i++; } char rez; int max = -2000000001; for(char i = 'x' ; i <= 'z' ; ++i) { if(f[i]>max && f[i]!=0) max=f[i] , rez = i; } cout << max << rez; return 0; }
