#include <bits/stdc++.h>
using namespace std; int f[300]; bool litmica(char ch) { return ch >= 'a' && ch <= 'z'; } bool litmare(char ch) { return ch >= 'A' && ch <= 'Z'; } int main() { char s[300]; cin.getline(s , 300); int i = 0; int cnt1 = 0 , cnt2 = 0; while(s[i]!='\0') { if(litmica(s[i])) cnt1++ , f[(int)s[i]]++; if(litmare(s[i])) cnt2++ , f[(int)tolower(s[i])]++; i++; } if(cnt1 > cnt2) { cout << '{'; bool ok = false; for(int i = 0 ; i < 150 ; ++i) { if(f[i]!=0 && ok) cout << ',' << (char)i; else if(f[i]!=0) cout << (char)i , ok=true; } cout << '}'; } else { cout << '{'; bool ok = false; for(int i = 0 ; i < 150 ; ++i) { if(f[i]!=0 && ok) cout << ',' << (char) (i - 32); else if(f[i]!=0) cout << (char) (i - 32) , ok=true; } cout << '}'; } return 0; }
