#include <bits/stdc++.h>
using namespace std; ifstream fin("vecini1.in"); ofstream fout("vecini1.out"); int main() { int n , m , a[102][102] , cnt = 0; fin >> n >> m; for(int i = 1 ; i <= n ; ++i) { for(int j = 1 ; j <= m ; ++j) { fin >> a[i][j]; a[0][j] = 1000001; a[n + 1][j] = 1000002; a[i][0] = 1000003; a[i][m + 1] = 1000004; } } for(int i = 1 ; i <= n ; ++i) { for(int j = 1 ; j <= m; ++j) { if(a[i][j-1] != a[i][j+1] && a[i][j-1] != a[i-1][j] && a[i][j-1] != a[i+1][j] && a[i][j+1] != a[i-1][j] && a[i-1][j] != a[i+1][j] && a[i][j+1] != a[i+1][j]) cnt++; } } fout << cnt; fin.close(); fout.close(); return 0; }
