#include <bits/stdc++.h>
using namespace std; ifstream fin("cifre5.in"); ofstream fout("cifre5.out"); int n , v[100001] , ok , d , poz , poz2 , sum , i2 , k , A[10001] , cnt; int main() { fin >> n >> k; for(int i = 1 ; i <= n ; i++) fin >> v[i]; sort(v + 1, v + n + 1); while(v[poz] == 0) poz++; if(poz == 1) poz -= k; poz += k - 1; poz2 = n; d = -1; i2 = 1; while (sum > 0 || i2 <= n) { for(int i = 1 ; i <= k && i2 <= n ; i++) { if(poz2 == poz && ok == 0 ) d = 1 , poz2 = 1 , ok = 1; else if (poz2 == poz - k + 1 && ok == 1) d = -1 , poz2 = poz , ok = 2; sum += v[poz2]; poz2 += d; i2++; } A[++cnt] = sum % 10; sum /= 10; } for(int i = cnt ; i >= 1 ; i--) fout << A[i]; return 0; }
